import React, { Component } from 'react';
import Link from 'next/link';
import ModalVideo from 'react-modal-video';

class MainBanner extends Component {

    state = {
        isOpen: false,
    }
    openModal = () => {
        this.setState({isOpen: true})
    }

    render() {
        return (
            <React.Fragment>
                <div className='hero-banner video-studio overly-6'>
                    <div className='video-background'>
                        <video autoPlay loop src={require('../assets/video/promo-video.mp4')} />
                    </div>

                    <div className='d-table'>
                        <div className='d-table-cell'>
                            <div className='container'>
                                <div className='row align-items-center'>
                                    <div className='col-lg-8'>
                                        <div className='main-banner-content'>
                                            <h1>Cognitive Behavioural Therapy</h1>
                                            <p>KA-Therapy provides a range of evidence-based psychotherapies to help you improve your psychological wellbeing </p>

                                            <Link href='/contact'>
                                                <a className='btn btn-primary'>Contact Us</a>
                                            </Link>
                                        </div>
                                    </div>

                                    {/* <div className='col-lg-4'>
                                        <div className='video-box'>
                                            <Link href='#play-video'>
                                                <a
                                                    onClick={e => {e.preventDefault(); this.openModal()}}
                                                    className='video-btn'
                                                > 
                                                    <i className='pe-7s-play'></i>
                                                </a>
                                            </Link>
                                        </div>
                                    </div> */}
                                </div>
                            </div>
                        </div>
                    </div>
                </div>

                {/* If you want to change the video need to update below videoID */}
                <ModalVideo 
                    channel='youtube' 
                    isOpen={this.state.isOpen} 
                    videoId='_ysd-zHamjk' 
                    onClose={() => this.setState({isOpen: false})} 
                />
            </React.Fragment>
        );
    }
}

export default MainBanner;
