import React, { Component } from 'react';

class ContactForm extends Component {

    state = {
        name: '',
        email: '',
        subject: '',
        phone: '',
        message: '',
        submitted: false
    }

    handleInputChange = (e) => {
        const target = e.target;
        const name = e.target.name;
        const value = target.value;
        this.setState({
            [name]: value
        })
    }

    handleSubmit = (e) => {
        e.preventDefault()
        const { name, email, subject, phone, message } = this.state;
        this.setState({
            submitted: true
        }, () => {
            sendContactForm(name, email, subject, phone, message)
            createNewContact(name, email, subject, phone, message)
            toast.success(`Thanks ${name}. We have received your message and will be in contact very soon.`);
        })
    }

    render() {
        return (
            <div className='contact-form'>
                <form id='contactForm'>
                    <div className='container'>
                        <div className='row'>
                            <div className='col-lg-6'>
                                <div className='form-group'>
                                    <input type='text' name='name' id='name' className='form-control' placeholder='Your Name' required />
                                </div>
                            </div>

                            <div className='col-lg-6'>
                                <div className='form-group'>
                                    <input type='email'  name='email' id='email' className='form-control' placeholder='Your Email' required />
                                </div>
                            </div>

                            <div className='col-lg-6'>
                                <div className='form-group'>
                                    <input type='text' name='phone_number' id='phone_number' className='form-control' placeholder='Phone' required />
                                </div>
                            </div>

                            <div className='col-lg-6'>
                                <div className='form-group'>
                                    <input type='text' name='msg_subject' id='msg_subject' className='form-control' placeholder='Subject' required />
                                </div>
                            </div>

                            <div className='col-lg-12 col-md-12'>
                                <div className='form-group'>
                                    <textarea name='message' className='form-control' id='message' rows='8' placeholder='Your Message' required></textarea>
                                </div>
                            </div>

                            <div className='col-lg-12 col-md-12'>
                                <button type='submit' className='btn btn-primary'>Send Message</button>
                            </div>
                        </div>
                    </div>
                </form>
            </div>
        );
    }
}

export default ContactForm;
